import "./Product.css";
import { useStateValue } from "./StateProvider";

function Product({id, title, image, price, rating }) {
const [{ basket }, dispatch] = useStateValue();
// console.log("this is" , basket)
const addToBasket = () => {
  // dispatch the item into the data layer
  dispatch({
    type: "ADD_TO_BASKET",
