import React from "react";
import "./App.css";
import Header from "./Header";
import Home from "./Home";
import Checkout from "./Checkout";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
function App() {
  return (
    <Router>
      <div className="app">
      {/* Header component  */}
      <Header />
        <Switch>
        <Route path="/checkout">
            {/* Checkout component*/}
            <Checkout />
          </Route>
          <Route path="/">
            {/* Home component*/}
            <Home />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
